import React, {useEffect, useState} from "react";
import GTest from "./components/GTest";

const App = () => {
    const [counter, setCounter] = useState(0);

    useEffect(() => {
        setCounter(0);
    }, []);
    return (
        <div className="App">
            <GTest name={"James"} lastName={"Doe"} dateOfBirth={"10-11-1993"}/>

            <button onClick={() => setCounter((prevCount) => prevCount - 1)}>-</button>
            <h2>{counter}</h2>
            <button onClick={() => setCounter((incrementCount) => incrementCount + 1)}>+</button>
        </div>
    );
};

export default App;
